import React, { useState } from "react"
import { Card, Button, Alert } from "react-bootstrap"
import { useAuth } from "../contexts/AuthContext"
import { Link, useHistory } from "react-router-dom"
import logo from "../assets/logo.png" ;
import "./imgStyles.css";

export default function Dashboard() {

  const [error, setError] = useState("")
  const { currentUser, logout } = useAuth()
  const history = useHistory()

  async function handleLogout() {
    setError("")

    try {
      await logout()
      history.push("/login")
    } catch {
      setError("Failed to log out")
    }
  }

  return (
    <>

      <Card>
      <img alt="logo" className="photo" src={logo}></img>
        <Card.Body>

          <h2 className="text-center mb-4">Fix My Mix Dashboard</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <strong>Spotify Email:</strong> {currentUser.email}
          <Link to="/update-profile" className="btn btn-primary w-100 mt-3">
            Update Profile
          </Link>
          <Button variant="link" onClick={handleLogout}>
          Log Out
        </Button>
        </Card.Body>
      </Card>
      <div className="w-100 text-center mt-2">
        {/* <Button variant="link" onClick={handleLogout}>
          Log Out
        </Button> */}
      </div>
      <Card style={{ width: '20rem' }}>
  {/* <Card.Img variant="top" src="holder.js/100px180" /> */}
  <iframe src="https://open.spotify.com/embed/playlist/37i9dQZF1DX0XUsuxWHRQd" width="300" height="380" frameborder="0" allowtransparency="true" allow="encrypted-media"></iframe>
  <Card.Body>
    <Card.Title>Recent Playlist</Card.Title>
    <Card.Text>
      Some quick example text to build on the card title and make up the bulk of
      the card's content.
    </Card.Text>
    <Button variant="primary">Go somewhere</Button>
  </Card.Body>
</Card>
    </>
  )
  }
